import React, { Component } from 'react';
import './card.css';


class Card extends Component {

    constructor (props) {
        super(props)

        this.state = {
            users: [],
            isFetch: true,
        }
    }

    componentDidMount() {
        fetch('https://randomuser.me/api')
            .then(res => res.json())
            .then(data => this.setState({ users: data.results, isFetch: false
        }))
    }

    render() {
        if (this.state.isFetch) {
            return 'Loading...'
        }

        const foto = this.state.users[0].picture.large
        const name = this.state.users[0].name.first
        const email = this.state.users[0].email

        return (
            <div className="container" id="identif">
                <div className="card">
                    <div className="profile">
                        <div className="circle"></div>
                        <img src={foto} alt=""></img>
                    </div>
                    <div className="info">
                        <h1 className="title" >{name}</h1>
                        <h3>{email}</h3>
                        <div className="chosse">
                            <button onClick={()=>{this.componentDidMount()}}>Chosse</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

//Movement Animation to happen
const container = document.querySelector(".container");
const card = document.querySelector(".card");
//Items
const profile = document.querySelector(".profile img");
const title = document.querySelector(".title");
const description = document.querySelector(".info h3");
const chosse = document.querySelector(".chosse");



var identif = document.getElementById('identif');
if(container) {

  //Moving Animation Event
  container.addEventListener("mousemove", (e) => {
    let xAxis = (window.innerWidth / 2 - e.pageX) / 25;
    let yAxis = (window.innerHeight / 2 - e.pageY) / -25;
    card.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`;
  });

  //Animate In
  container.addEventListener("mouseenter", (e) => {
    card.style.transition = "none";
    //Popout
    title.style.transform = "translateZ(150px)";
    profile.style.transform = "translateZ(180px) rotateZ(-360deg)";
    description.style.transform = "translateZ(150px)";
    chosse.style.transform = "translateZ(150px)";
  });

  //Animate Out
  container.addEventListener("mouseleave", (e) => {
    card.style.transition = "all 0.5s ease";
    card.style.transform = `rotateY(0deg) rotateX(0deg)`;
    //Popback
    title.style.transform = "translateZ(0px)";
    profile.style.transform = "translateZ(0px) rotateZ(0deg)";
    description.style.transform = "translateZ(0px)";
    chosse.style.transform = "translateZ(0px)";
  });
};

// window.onload = animate();
// setTimeout(animate,2000);

export default Card;
