import React, { Component } from 'react';
import './card.css';


class Card extends Component {
    constructor (props) {
        super(props)

        this.state = {
            users: [],
            isFetch: true,
        }
    }

    componentDidMount() {
        fetch('https://randomuser.me/api')
            .then(res => res.json())
            .then(data => this.setState({ users: data.results, isFetch: false
        }))
    }

    render() {
        setTimeout(function() { //Start the timer   
            if (this.state.isFetch) {
                return 'Loading...'
            }
        }.bind(this), 1000)
        const foto = this.state.users[0].picture.large
        const name = this.state.users[0].name.first
        const cell = this.state.users[0].cell
        const email = this.state.users[0].email

        return (

            <div className="container">
                <div className="card">
                    <div className="sneaker">
                        <div className="circle"></div>
                        <img src={foto} alt=""></img>
                    </div>
                    <div className="info">
                        <h1>{name}</h1>
                        <h3>{cell}</h3>
                        <h3>{email}</h3>
                        <div className="purchase">
                            <button onClick={()=>{this.componentDidMount()}}>Purchase</button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default Card;
