import './App.css';
import Card from './components/card';

function App() {


  //Movement Animation to happen
  const card = document.querySelector(".card");
  const container = document.querySelector(".container");
  //Items
  const profile = document.querySelector(".profile img");
  const title = document.querySelector(".title");
  const description = document.querySelector(".info h3");
  const chosse = document.querySelector(".chosse");

  //Moving Animation Event
  container.addEventListener("DOMContentLoaded", "mousemove", (e) => {
    let xAxis = (window.innerWidth / 2 - e.pageX) / 25;
    let yAxis = (window.innerHeight / 2 - e.pageY) / 25;
    card.style.transform = `rotateY(${xAxis}deg) rotateX(${yAxis}deg)`;
  });

  //Animate In
  container.addEventListener("DOMContentLoaded", "mouseenter", (e) => {
    card.style.transition = "none";
    //Popout
    title.style.transform = "translateZ(150px)";
    profile.style.transform = "translateZ(200px) rotateZ(-360deg)";
    description.style.transform = "translateZ(125px)";
    chosse.style.transform = "translateZ(75px)";
  });

  //Animate Out
  container.addEventListener("DOMContentLoaded", "mouseleave", (e) => {
    card.style.transition = "all 0.5s ease";
    card.style.transform = `rotateY(0deg) rotateX(0deg)`;
    //Popback
    title.style.transform = "translateZ(0px)";
    profile.style.transform = "translateZ(0px) rotateZ(0deg)";
    description.style.transform = "translateZ(0px)";
    chosse.style.transform = "translateZ(0px)";
  });


  return (
    <div className="App">
      <header className="App-header">
        <Card />
      </header>
    </div>
  );
}

export default App;
